mapper.delete:
delete from 
   rf_comment 
where 
   recipe_num = #{recipe_num} 
   and comment_num = #{comment_num}
   and user_id = #{user_id}

mapper.insert:
insert into 
	rf_comment (
		comment_num
		, recipe_num
		, user_id
		, comment_text
	)
values (
		rf_comment_seq.nextval
		, #{recipe_num}
		, #{user_id}
		, #{comment_text}
)
