mapper.insert:
insert into rf_user (
user_id
, user_pw
, user_name
, user_email
, user_phone
)
values (
#{user_id}
, #{user_pw}
, #{user_name}
, #{user_email}
, #{user_phone}
)

mapper.update:
update rf_user
set
user_name = #{user_name}
,user_email = #{user_email}
,user_phone=#{user_phone}

	,user_pw =#{user_pw}

where
user_id = #{user_id}

mapper.insert:
insert into rf_refrigerator (
user_id
, refrigerator_num
, refrigerator_need
, refrigerator_date
)
values (
#{user_id}
, rf_refrigerator_seq.nextval
, #{refrigerator_need}
, #{refrigerator_date}
)
