mapper.insert:
insert into rf_zzim (
zzim_num
, recipe_num
, zzim_id
, zzim_name
, zzim_originalfile
,
zzim_savedfile
, zzim_need
, zzim_howto
, zzim_type
, zzim_icon
)
values (
zzim_num_seq.nextval
, #{recipe_num}
, #{zzim_id}
, #{zzim_name}
,
#{zzim_originalfile}
, #{zzim_savedfile}
, #{zzim_need}
, #{zzim_howto}
,
#{zzim_type}
, #{zzim_icon}
)

mapper.update:
update
	rf_zzim
set
	zzim_name = #{zzim_name}
	, zzim_need = #{zzim_need}
	, zzim_howto = #{zzim_howto}
	, zzim_type = #{zzim_type}
	, zzim_icon = #{zzim_icon}

	, zzim_originalfile = #{zzim_originalfile}


	, zzim_savedfile = #{zzim_savedfile}

mapper.delete:
delete from rf_zzim
where
zzim_num = #{zzim_num}
and zzim_id = #{zzim_id}
